package private

const (
	DefaultIpfsUrl    = "localhost:5001"
	IPFS              = "ipfs"
	Swarm             = "swarm"
	DefaultRSAPrivKey = ""
)

type Config struct {
	RemoteDBParams string
	RemoteDBType   string
	PrivateKey     string
}

func DefaultConfig() Config {
	return Config{
		RemoteDBType:   IPFS,
		RemoteDBParams: DefaultIpfsUrl,
		PrivateKey:     DefaultRSAPrivKey,
	}
}
